import React, {Component} from 'react';
import classes from './ListMenu.module.css';

import TodoList from '../TodoList/TodoList';
import ListTab from '../../Components/ListTab/ListTab'

class ListMenu extends Component {


	state = {
		todoLists: [
        {
          id: 123,
          title: 'Grocery list',
          path: '/grocery-list'
        },
       {
       	  id: 543,
          title: 'Prject Tasks',
          path: '/project-tasks'
        },
      ],
      selectedTodoListId: 0,
      selectedTodoListData: {}
	}

	showToDoList = (todoListId) => {
		let todoListData = this.state.todoLists.find((el)=>el.id==todoListId);
		this.setState({ selectedTodoListId: todoListId,
						selectedTodoListData: todoListData })
	}


	render(){

		let ToDoLists = [];

		// if(this.state.selectedTodoListId == 0){
		// 	ToDoLists = <h3>You need to create a new ToDo List...</h3>
		// }

			ToDoLists.push(this.state.todoLists.map(el=>
				<TodoList id={el.id} title={el.title}/>
				))
		

		let ToDoList = ToDoLists.find((el)=> el.id==this.state.selectedTodoListId );

		let listTabs = this.state.todoLists.map(el=>
			<ListTab 
				title={el.title}
				showToDoList={() => this.showToDoList(el.id)}/>
			)
		
		

		return(
			<div>
				<div>
					<ul className={classes.listMenu}>
						{listTabs}
					</ul>
					<button>+New List</button>
				</div>
					{ToDoList}
			</div>		
			)
	}
}

export default ListMenu;
