import React, {Component} from 'react';

import classes from './TodoList.module.css'
import Task from '../../Components/Task/Task';

class TodoList extends Component{

	state = {
		listTitle: "My List #1",
		currentInputText: '',
		tasks: [
			{id: 0, taskText: 'Get lots of cat food', checked: true},
			{id: 1, taskText: 'Rescue a stray cat', checked: false},
		]
	}

	taskInputChangeHandler = (event) => {
		let text = event.target.value;
		this.setState({
			...this.state,
			currentInputText: text
		})
	}

	addTask = () => {
		alert('ye');
	}

	render(){

		let tasks = this.state.tasks.map(el=>(
			<Task id={el.id} text={el.taskText} checked={el.checked}/>
		))

		return(
			<div>
				<h2>{this.state.listTitle}</h2>
				<input type="text" onChange={(event)=>this.taskInputChangeHandler(event)} size="40" placeholder="Write your next task here..."/>
				<button onclick={(event)=>this.addTask(event)}>Add</button>
				<ul className={classes.list}>
					{tasks}
				</ul>
			</div>
			)
	}
}

export default TodoList;
