import React, {Component} from 'react';
import { withStore } from "../../store/store";
import classes from './MainScreen.module.css';
import { Route } from 'react-router-dom';

// Screens: 
import TodoListScreen from './Screens/TodoListScreen/TodoListScreen';


class MainScreen extends Component {

	render(){


		let currentUrl = window.location.pathname;
		let currentList = this.props.store.get("listsMenu")[this.props.store.get("listsMenu").findIndex(list=>list.path==currentUrl)]
		
		return(
			<div>
				<Route 
					path={currentUrl}
					render= { () => ( <TodoListScreen currentList={currentList}/> )}
				/>
			</div>
			)
	}
}

export default withStore(MainScreen);
