import React, {Component} from 'react';

import classes from './TodoList.module.css'
import Task from '../../Components/Task/Task';

class TodoList extends Component{

	state = {
		listTitle: "My List #1",
		currentInputText: '',
		tasks: [
			{id: 0, taskText: 'Get lots of cat food', checked: true},
			{id: 1, taskText: 'Rescue a stray cat', checked: false},
		]
	}

	taskInputChangeHandler = (event) => {
		let text = event.target.value;
		this.setState({
			...this.state,
			currentInputText: text
		})
	}

	addTask = (event) => {
		let taskObj = {
			id: this.state.tasks.length,
			taskText: this.state.currentInputText,
			checked: false
		}
		let tasks = [...this.state.tasks];
		tasks.push(taskObj);
		this.setState({
			...this.state,
			tasks: tasks,
			currentInputText: ''
		})
	}

	checkboxHandler = (id) => {
		let newTaskCheck = !this.state.tasks[id].checked;
			this.setState({
				...this.state,
				// tasks[id].checked: newTaskCheck
			})
		}

	render(){

		let tasks = this.state.tasks.map(el=>(
			<Task id={el.id} text={el.taskText} checked={el.checked} checkboxHandler={this.checkboxHandler}/>
		))

		return(
			<div>
				<h2>{this.state.listTitle}</h2>
				<input type="text" onChange={(event)=>this.taskInputChangeHandler(event)} size="40" value={this.state.currentInputText} placeholder="Write your next task here..."/>
				<button onClick={(event)=>this.addTask(event)}>Add</button>
				<ul className={classes.list}>
					{tasks}
				</ul>
			</div>
			)
	}
}

export default TodoList;
