import React, {Component} from 'react';
import classes from './ListMenu.module.css';

import TodoList from '../TodoList/TodoList';
import ListTab from '../../Components/ListTab/ListTab'

class ListMenu extends Component {


	state = {
		todoLists: [
        {
          id: 123,
          title: 'Grocery list',
          path: '/grocery-list',
          isShown: false
        },
       {
       	  id: 543,
          title: 'Prject Tasks',
          path: '/project-tasks',
          isShown: true
        },
        {
       	  id: 54523,
          title: 'hgfdhgf Tafdresfdgsks',
          path: '/project-tasks',
          isShown: false
        },
      ],
      selectedTodoListData: {}
	}

	showToDoList = (todoListId) => {

		let newTodoLists = [...this.state.todoLists];

		newTodoLists.find((el)=>el.id==todoListId).isShown=true;

		console.log(newTodoLists);

		// let newTodoLists = [];

		// for(let i = 0; i < this.state.todoLists.length; i++){
		// 	if(this.state.todoLists[i].id!==todoListId){
		// 		let obj = this.state.todoLists[i];
		// 		obj.isShown=false;
		// 		newTodoLists.push(obj);
		// 	}
		// }
		
		// newTodoLists.push(currentTodoListData);
		// this.setState({todoLists:newTodoLists})
	}


	render(){
		
		let ToDoLists = this.state.todoLists.map(el=>
			<TodoList 
				isShown={el.isShown}
				title={el.title}
				id={el.id}/>
		)

		let listTabs = this.state.todoLists.map(el=>
			<ListTab 
				title={el.title}
				showToDoList={() => this.showToDoList(el.id)}/>
			)
				
		return(
			<div>
				<div>
					<ul className={classes.listMenu}>
						{listTabs}
					</ul>
					<button>+New List</button>
				</div>
					{ToDoLists}
			</div>		
			)
	}
}

export default ListMenu;
