import React from 'react';

import classes from './Task.module.css';

	const Task = (props) => {

		checkboxHandler = () => {
			alert('a');
		}

		return(
				<li id={props.id} className={classes.task}>
					<input 
						type="checkbox" 
						onChange={checkboxHandler} 
						checked={props.checked}/>
					{props.text}
				</li>
			)
	}

export default Task;
