import React, {Component} from 'react';
import classes from './ListMenu.module.css';

import TodoList from '../TodoList/TodoList';
import ListTab from '../../Components/ListTab/ListTab'

class ListMenu extends Component {


	state = {
		todoLists: [
        {
          id: 123,
          title: 'Grocery list',
          path: '/grocery-list'
        },
       {
       	  id: 543,
          title: 'Prject Tasks',
          path: '/project-tasks'
        },
      ],
      selectedTodoListId: 0,
      selectedTodoListData: {}
	}

	showToDoList = (todoListId) => {
		this.setState({selectedTodoListId: todoListId})
				console.log(this.state.selectedTodoListId)

		let todoListData = this.state.todoLists.find((el)=>el.id==this.state.selectedTodoListId);
		this.setState({selectedTodoListData: todoListData});
	}


	render(){
		console.log(this.state.selectedTodoListId)

		let ToDoList = <TodoList data={this.state.selectedTodoListData}/>

		let listTabs = this.state.todoLists.map(el=>
			<ListTab 
				title={el.title}
				showToDoList={() => this.showToDoList(el.id)}/>
			)
		
		

		return(
			<div>
				<div>
					<ul className={classes.listMenu}>
						{listTabs}
					</ul>
					<button>+New List</button>
				</div>
					{ToDoList}
			</div>		
			)
	}
}

export default ListMenu;
